mapper.select:
select * from toon
order by up desc
limit #{cri.startPage},#{cri.perPageNum}

select * from toon
order by t_views desc
limit #{cri.startPage},#{cri.perPageNum}

select * from toon where 1=1
and t_type like concat('%',#{cri.genre},'%')
order by up desc limit #{cri.startPage},#{cri.perPageNum}
